package de.conterra.babelfish.sample.feature;

import de.conterra.babelfish.plugin.v10_02.feature.Feature;
import de.conterra.babelfish.plugin.v10_02.object.feature.GeometryFeatureObject;
import de.conterra.babelfish.plugin.v10_02.object.geometry.Point;
import de.conterra.babelfish.plugin.v10_02.object.renderer.RendererObject;
import de.conterra.babelfish.plugin.v10_02.object.renderer.UniqueValue;
import de.conterra.babelfish.plugin.v10_02.object.renderer.UniqueValueRenderer;
import de.conterra.babelfish.plugin.v10_02.object.symbol.PictureMarkerSymbol;
import de.conterra.babelfish.util.DataUtils;
import de.conterra.babelfish.util.GeoUtils;
import org.geotools.geometry.DirectPosition2D;
import org.geotools.geometry.iso.primitive.PointImpl;
import org.opengis.referencing.FactoryException;
import org.opengis.referencing.crs.CoordinateReferenceSystem;

import java.awt.*;
import java.util.LinkedHashSet;
import java.util.Set;

/**
 * defines {@link SimpleFeatureLayer} with {@link Point}s
 *
 * @author ChrissW-R1
 * @version 0.4.0
 * @since 0.1.0
 */
public class SimplePointLayer
		extends SimpleFeatureLayer<Point, GeometryFeatureObject<Point>> {
	/**
	 * a {@link Set} of all {@link Point}s, which are in a {@link SimplePointLayer}
	 *
	 * @since 0.1.0
	 */
	private static final Set<Feature<GeometryFeatureObject<Point>>> points = new LinkedHashSet<>();
	
	static {
		CoordinateReferenceSystem crs;
		try {
			crs = GeoUtils.decodeCrs("4326");
		} catch (FactoryException e) {
			crs = null;
		}
		
		SimplePointLayer.createPoint(crs, 51.9350718, 7.6513469, "railway_crossing", null);
		SimplePointLayer.createPoint(crs, 51.9352402, 7.6498429, "railway_crossing", null);
		SimplePointLayer.createPoint(crs, 51.9348068, 7.6517225, "street_lamp", null);
		SimplePointLayer.createPoint(crs, 51.9345782, 7.6520727, "street_lamp", null);
		SimplePointLayer.createPoint(crs, 51.9343469, 7.6524293, "street_lamp", null);
		SimplePointLayer.createPoint(crs, 51.9346670, 7.6522797, "bollard", null);
		SimplePointLayer.createPoint(crs, 51.9345116, 7.6525727, "bollard", null);
		SimplePointLayer.createPoint(crs, 51.9342212, 7.6526496, "bus_stop", "Friedenspark");
		SimplePointLayer.createPoint(crs, 51.9342608, 7.6530949, "bus_stop", "Friedenspark");
		SimplePointLayer.createPoint(crs, 51.9353012, 7.6502938, "tree", "Friedenskastanie");
	}
	
	/**
	 * constructor, with all necessary attributes
	 *
	 * @param id   the unique identifier
	 * @param name the name shown to the user
	 * @param desc the layer description
	 * @since 0.1.0
	 */
	public SimplePointLayer(int id, String name, String desc) {
		super(id, name, desc);
	}
	
	@Override
	public Class<Point> getGeometryType() {
		return Point.class;
	}
	
	@Override
	public RendererObject getRenderer() {
		int picSize = 26;
		
		UniqueValueRenderer result = new UniqueValueRenderer(new PictureMarkerSymbol(DataUtils.toImage(DataUtils.decodeBase64("")).getScaledInstance(picSize, picSize, Image.SCALE_SMOOTH)), "Other " + this.getName());
		
		result.addField(SimpleField.TYPE_FIELD);
		
		result.addUniqueValue(new UniqueValue("street_lamp", "street_lamp", new PictureMarkerSymbol(DataUtils.toImage(DataUtils.decodeBase64("")).getScaledInstance(picSize, picSize, Image.SCALE_SMOOTH))));
		result.addUniqueValue(new UniqueValue("bus_stop", "bus_stop", new PictureMarkerSymbol(DataUtils.toImage(DataUtils.decodeBase64("")).getScaledInstance(picSize, picSize, Image.SCALE_SMOOTH))));
		
		return result;
	}
	
	@Override
	public Set<? extends Feature<GeometryFeatureObject<Point>>> getFeatures() {
		return new LinkedHashSet<>(SimplePointLayer.points);
	}
	
	/**
	 * creates a new {@link Point} and adds it to the {@link Set}
	 *
	 * @param crs  the {@link CoordinateReferenceSystem}
	 * @param x    the x-ordinate
	 * @param y    the y-ordinate
	 * @param type the type {@link String}
	 * @param name the name of the {@link Feature}
	 * @since 0.4.0
	 */
	private static void createPoint(CoordinateReferenceSystem crs, double x, double y, String type, String name) {
		GeometryFeatureObject<Point> point = new GeometryFeatureObject<>(new Point(new PointImpl(new DirectPosition2D(crs, x, y))));
		point.addAttribute(SimpleField.TYPE_FIELD, type);
		
		if (!(name == null || name.isEmpty())) {
			point.addAttribute(SimpleField.NAME_FIELD, name);
		}
		
		SimplePointLayer.points.add(new SimpleFeature<>(point));
	}
}
